tinymce.PluginManager.add("readmore", function (e) {
    e.addCommand("readmore", function () {
        e.execCommand("mceInsertContent", !1, "<hr id=\"openmvc-readmore\" style=\"border:red dashed 1px;\">")
    }), e.addButton("readmore", {
        text: "Lire la suite",
        icon: !1,
        tooltip: "Lire la suite",
        cmd: "readmore"
    }), e.addMenuItem("readmore", {
        icon: !1,
        text: "Lire la suite",
        cmd: "readmore",
        context: "insert"
    })
});